import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';

const today = new Date()

const user = {
          name: 'Sandra',
          last: 'Alpizar',
          email: 'sandra.alpizar@gmail.com',
          dob: new Date(2020, 12, 31)};

var fullname = () => {
  return user.name + ' ' + user.last;
}

const element = <h1>Hello {user.name}!</h1>;
const old_greetings = (
  <h2>
    Hello {fullname()}! Today is {today.getMonth()}/{today.getDate()}/{today.getYear()}
  </h2>);
const meme = 'https://mamasgeeky.com/wp-content/uploads/2020/03/coronavirus-meme-1.jpg'

function buildGreetings (user){
  if(user){
    return (
      <section>
        <h2>
          Hello {fullname()}! Today is {today.getMonth()}/{today.getDate()}/{today.getYear()}
        </h2>
        <img src = {meme}/>
      </section>);
  }
  return (<h2>Hello Stranger</h2>);
}




ReactDOM.render(
  /*
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  */
  //element
  buildGreetings(user),
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
